package BulletinBoard;

public class Confirm {
		String item       = null;
		String content    = null;
		
		public Confirm(String item, String content) {
			// TODO Auto-generated constructor stub
			this.item = item;
			this.content = content;
		}
		public String getItem() {
			return item;
		}
		public String getContent() {
			return content;
		}
}
